using System;
using System.Collections.Generic;
using System.Text;

namespace PaymentCenter.ConsoleApp
{
    public class RSATest
    {
        public static void Test()
        {
            //2048 公钥
             string publicKey =
                "";
            //2048 私钥
            string privateKey =
                "";
            
            string str = "博客园 http://www.cnblogs.com/";

            Console.WriteLine("原始字符串：" + str);

            //加密
            string enStr = Infrastructure.Tools.RSATool.Encrypt(Encoding.UTF8, publicKey, str);

            Console.WriteLine("加密字符串：" + enStr);

            //解密
            string deStr = Infrastructure.Tools.RSATool.Decrypt(Encoding.UTF8, privateKey, enStr);

            Console.WriteLine("解密字符串：" + deStr);

            //私钥签名
            string signStr = Infrastructure.Tools.RSATool.Sign(Infrastructure.Tools.RSAType.RSA2, privateKey, str, Encoding.UTF8);

            Console.WriteLine("字符串签名：" + signStr);

            //公钥验证签名
            bool signVerify = Infrastructure.Tools.RSATool.Verify(Infrastructure.Tools.RSAType.RSA2, publicKey, str, signStr, Encoding.UTF8);

            Console.WriteLine("验证签名：" + signVerify);
        }
    }
}
